var fs = require('fs');
var fn=["India2011.csv","IndiaSC2011.csv","IndiaST2011.csv"];
var obj={};
for(var i=0;i<fn.length;i++)
{
  var f=fs.readFileSync(fn[i],"utf8");
  var l=f.split("\n");
  var li=l[0].split(",");
  var i1=li.indexOf("Age-group"),
      i2=li.indexOf("Total/ Rural/ Urban"),
      i3=li.indexOf("Literate - Persons");
  for(var j=1;j<l.length;j++)
  {
    cl=l[j].split(",");
    if(cl[i1]!="0-6" && cl[i2]==="Total")
    {
      if(obj[cl[i1]==='\0'])
        obj[cl[i1]]=cl[i3];
      else {
        obj[cl[i1]]+=cl[i3];
      }
    }
  }
  console.log(obj);
}
